%spark
val rawArtistData = spark.read.textFile("/data/profiledata_06-May-2005/artist_data.txt")
val artistByID = rawArtistData.flatMap { line =>
    val (id, name) = line.span(_ != '\t')
    if (name.isEmpty) None
    else {
        try Some((id.toInt, name.trim))
        catch {
            case _: NumberFormatException => None
        }
    }
}.toDF("id", "name")

%spark
val rawArtistAlias = spark.read.textFile("/data/profiledata_06-May-2005/artist_alias.txt")
val artistAlias = rawArtistAlias.flatMap { line =>
    val Array(artist, alias) = line.split('\t')
    if (artist.isEmpty) None
    else Some((artist.toInt, alias.toInt))
}.collect().toMap
